import _ from 'lodash'
import moment from 'moment'
import wrapCurrentDate from '/imports/client/lib/wrap-current-time'
import { Printd } from 'printd'
import Notify from '/imports/client/lib/notify'
import Lookup from '../lib/Lookup-Value'
import { lookupType } from '/imports/utils/lookup-methods'
//
import { findExchangeStudents } from '/imports/modules/school/api/report/exchangeStudent'
import { dateRangePickerOpts } from '/imports/client/lib/date-range-picker-opts'

const numeral = require('numeral')
export default {
  data() {
    return {
      loading: false,
      docOpts: Lookup.type,
      tableData: [],
      totalAll: 0,
      totalFemale: 0,
      reportDate: moment().toDate(),
      titles: [
        { label: 'Student', prop: 'student' },
        { label: 'Gender', prop: 'gender', width: '100px' },
        { label: 'Pay Date', prop: 'payDate' },
        { label: 'End Date', prop: 'endPayDate' },
      ],
      form: {
        opts:[ moment().toDate(),moment().toDate()],
      },
      rules: {
        opts: [{ required: true }],
      },
      pickerOptions: dateRangePickerOpts
    }
  },
  mounted() {
    this.d = new Printd()
    // this.getTypeData()
  },
  methods: {
    getTypeData() {
      lookupType
        .callPromise()
        .then(result => {
          this.docOpts = result
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },

    handleSubmit() {
      this.loading = true
      // let sDate = moment(this.form.opts[0]).startOf('day')
      // let eDate = moment(this.form.opts[1]).endOf('day')
      // let selector = {
      //   tranDate: {
      //     $gte: sDate,
      //     $lte: eDate,
      //   },
      // }

      this.tableData = []
     this.totalAll = 0
      this.totalFemale = 0

      findExchangeStudents
        .callPromise({selector:this.form})
        .then(result => {
          if (result.length > 0) {
            this.tableData = result

            _.forEach(result, o => {
              this.totalAll += 1
              if (o.gender == 'Female') {
                this.totalFemale += 1
              }
            })
          } else {
            this.tableData = []
            this.totalAll = 0
            this.totalFemale = 0
          }
          this.loading = false
        })
        .catch(error => {
          Notify.error({ message: error.reason })
        })
    },
    handlePrint() {
      const reportCSS = `
      .logo>img {
          float:left;
          // position: absolute;
        }
      img{
         width: 140px;
          height: 100px;
      }
        .report-header>header{
          content: "";
          clear: both;
          // display: table;
        }
      .header {

          text-align: center;
          padding-bottom: 10px;
          width: 80%;
          margin-left:20px;
        }
        .header >.headerKhmer {

            font-size: 30px;
             color: darkgray;
            font-weight: 500;
            padding-top: 10px;
            font-family: 'Moul', PingFang SC, Hiragino Sans GB, Microsoft YaHei, SimSun, sans-serif !important;
        }
        .header >.headerEn {
          font-size: 25px;
          font-family: Times New Roman;
          color: darkgray;

        }
        .table-content {
          border-collapse: collapse;
          width: 100%;
        }
        .info-class {
          position: relative;
          height:10vh;
      }

      .info-class>.clLeft {
          font-size: 14px;
          position: absolute;
          top: 35%;
          max-width: 20%;
          left: 5%;
      }

      .info-class>.clCenter {
          font-size: 14px;
          position: absolute;
          margin-left: 10vh;
          top: 35%;
          left: 25%;
      }

      .info-class>.clRight {
          font-size: 14px;
          position: absolute;
          left: 70%;
          top: 35%;
      }
      .tableShow {
          font-family: sans-serif;
          font-size: 12px;

      }
       .tableShow .table-content,
      th,
      td {
          // border: 1px solid #ddd;
           border: 0.1px solid #606266;
          // border-bottom: 1px solid #ddd;
          border-bottom: 0.1px solid #606266;
          padding: 5px;
      }

       .tableShow th {
          background-color: #ddd;
        }

        .title {
            font-size:14px;
            text-align: center;
            font-weight: 700;
            color: black;
        }

        .info-header {
            margin-top: 2vh;
            height: 4vh;
            text-align: center;
            font-size: 14px;
            font-weight: 500;
            font-style: initial oblique;
        }
        // hide repeat header
        thead
        {
            display: table-row-group;
        }
      `
      this.d.print(document.getElementById('tableStudent'), reportCSS)
    },

    formatDate(val) {
      return moment(val).format('L')
    },
    formatNum(val) {
      return numeral(val).format('0,0.00')
    },
    // function loop Date
    loopDate(from, to) {
      let data = []

      for (let m = from; m < to; m.setMonth(m.getMonth() + 1)) {
        data.push({ date: this.formatDate(m) })
      }

      return data
    },
  },
}
