import _ from 'lodash'
import moment from 'moment'
import wrapCurrentDate from '/imports/client/lib/wrap-current-time'
import { Printd } from 'printd'
import Notify from '/imports/client/lib/notify'
import Lookup from '../lib/Lookup-Value'
import { lookupType } from '/imports/utils/lookup-methods'
//
import { findDailyReport, findIncomeExpend } from '/imports/modules/school/api/Income/methods'

const numeral = require('numeral')
export default {
  data() {
    return {
      loading: false,
      docOpts: Lookup.type,
      tableData: [],
      tableInEx: [],
      totalUsd: 0,
      totalKhr: 0,
      usd: 0,
      khr: 0,
      reportDate: moment().toDate(),
      titles: [
        { label: 'Student', prop: 'student' },
        { label: 'Gender', prop: 'gender', width: '100px' },
        { label: 'Pay Date', prop: 'payDate' },
        { label: 'End Date', prop: 'endPayDate' },
      ],
      form: {
        opts: moment().toDate(),
      },
      rules: {
        opts: [{ required: true }],
      },
    }
  },
  mounted() {
    this.d = new Printd()
    // this.getTypeData()
  },
  methods: {
    getTypeData() {
      lookupType
        .callPromise()
        .then(result => {
          this.docOpts = result
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },

    handleSubmit() {
      this.loading = true
      let sDate = wrapCurrentDate(moment(this.form.opts))
      this.reportDate = sDate
      sDate = moment(sDate).format('YYYY-MM-DD')
      let eDate = wrapCurrentDate(moment(this.form.opts).add(1, 'days'))
      eDate = moment(eDate).format('YYYY-MM-DD')
      let selector = {
        tranDate: {
          $gte: new Date(sDate),
          $lte: new Date(eDate),
        },
        referenceType: { $nin: ['Income', 'Expend'] },
      }
      this.tableData = []
      this.tableInEx = []
      this.totalUsd = 0
      this.totalKhr = 0
      ;(this.usd = 0), (this.khr = 0)
      // let currentDate = wrapCurrentDate(this.form.opts)
      // currentDate = this.formatDate(currentDate)
      findDailyReport
        .callPromise({ selector })
        .then(result => {
          if (result) {
            this.tableData = result
            _.forEach(result, o => {
              this.totalUsd += o.totalUsd
              this.totalKhr += o.totalKhr
            })
          }
          this.loading = false
        })
        .catch(error => {
          Notify.error({ message: error })
        })

      // Income And Expend
      let selectorInEx = {
        tranDate: {
          $gte: new Date(sDate),
          $lte: new Date(eDate),
        },
      }
      findIncomeExpend
        .callPromise({ selector: selectorInEx })
        .then(result => {
          if (result) {
            _.forEach(result, o => {
              this.usd += o.usd
              this.khr += o.khr

              if (o.referenceType == 'Expend') {
                o.usd = -1 * o.usd
                o.khr = -1 * o.khr
              }
              this.tableInEx.push(o)
            })
          }
          this.loading = false
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },
    handlePrint() {
      const reportCSS = `
      .logo>img {
          float:left;
          // position: absolute;
        }
      img{
         width: 140px;
          height: 100px;
      }
        .report-header>header{
          content: "";
          clear: both;
          // display: table;
        }
      .header {

          text-align: center;
          padding-bottom: 10px;
          width: 80%;
          margin-left:20px;
        }
        .header >.headerKhmer {

            font-size: 30px;
             color: darkgray;
            font-weight: 500;
            padding-top: 10px;
            font-family: 'Moul', PingFang SC, Hiragino Sans GB, Microsoft YaHei, SimSun, sans-serif !important;
        }
        .header >.headerEn {
          font-size: 25px;
          font-family: Times New Roman;
          color: darkgray;

        }
        .table-content {
          border-collapse: collapse;
          width: 100%;
        }
        .info-class {
          position: relative;
          height:10vh;
      }

      .info-class>.clLeft {
          font-size: 14px;
          position: absolute;
          top: 35%;
          max-width: 20%;
          left: 5%;
      }

      .info-class>.clCenter {
          font-size: 14px;
          position: absolute;
          margin-left: 10vh;
          top: 35%;
          left: 25%;
      }

      .info-class>.clRight {
          font-size: 14px;
          position: absolute;
          left: 70%;
          top: 35%;
      }
      .tableShow {
          font-family: sans-serif;
          font-size: 12px;

      }
       .tableShow .table-content,
      th,
      td {
          // border: 1px solid #ddd;
           border: 0.1px solid #606266;
          // border-bottom: 1px solid #ddd;
          border-bottom: 0.1px solid #606266;
          padding: 5px;
      }

       .tableShow th {
          background-color: #ddd;
        }

        .title {
            font-size:14px;
            text-align: center;
            font-weight: 700;
            color: black;
        }

        .info-header {
            margin-top: 2vh;
            height: 4vh;
            text-align: center;
            font-size: 14px;
            font-weight: 500;
            font-style: initial oblique;
        }
        // hide repeat header
        thead
        {
            display: table-row-group;
        }
      `
      this.d.print(document.getElementById('tableStudent'), reportCSS)
    },

    formatDate(val) {
      return moment(val).format('L')
    },
    formatNum(val) {
      return numeral(val).format('0,0.00')
    },
    // function loop Date
    loopDate(from, to) {
      let data = []

      for (let m = from; m < to; m.setMonth(m.getMonth() + 1)) {
        data.push({ date: this.formatDate(m) })
      }

      return data
    },
  },
}
