import _ from 'lodash'
import Msg from '/imports/client/lib/message'
import Notify from '/imports/client/lib/notify'
// lib
import LookupValue from '../lib/Lookup-Value'

import { lookupRole, lookupBranch } from '/imports/utils/lookup-methods'
import { validateUserExist } from '/imports/utils/validate-methods'
import { findOneUser, updateUser } from '../../api/users/methods'

export default {
  name: 'UserEdit',
  props: {
    updateId: {
      type: String,
      default: null,
    },
  },
  data() {
    // const _id = this.$route.params._id
    const _id = this.updateId

    // Custom validate
    const validateUsername = (rule, value, callback) => {
      if (value) {
        validateUserExist
          .callPromise({
            _id: { $ne: _id },
            username: value,
          })
          .then(result => {
            if (result) {
              callback(new Error('Username exist'))
            } else {
              callback()
            }
          })
          .catch(error => {
            Notify.error({ message: error })
          })
      }
    }

    const validateEmail = (rule, value, callback) => {
      if (value) {
        validateUserExist
          .callPromise({
            _id: { $ne: _id },
            emails: { $elemMatch: { address: value } },
          })
          .then(result => {
            if (result) {
              callback(new Error('Email exist'))
            } else {
              callback()
            }
          })
          .catch(error => {
            Notify.error({ message: error })
          })
      }
    }

    const validatePassword = (rule, value, callback) => {
      if (value) {
        if (this.form.confirmPassword !== '') {
          this.$refs.form.validateField('confirmPassword')
        }
        callback()
      }
    }

    const validateConfirmPassword = (rule, value, callback) => {
      if (value && value !== this.form.password) {
        callback(new Error("The password don't match!"))
      } else {
        callback()
      }
    }

    return {
      loading: false,
      statusOpts: LookupValue.status,
      roleOpts: [],
      // LookupValue.roles,
      branchPermissionOpts: [],
      form: {
        _id: '',
        fullName: '',
        username: '',
        email: '',
        status: '',
        password: '',
        confirmPassword: '',
        allowedBranches: [],
        roles: [],
      },
      rules: {
        fullName: [{ required: true, message: 'Full name is required' }],
        username: [
          { required: true, message: 'Username is required' },
          {
            min: 5,
            max: 20,
            message: 'Length should be 5 to 20',
            trigger: 'blur',
          },
          { validator: validateUsername, trigger: 'blur' },
        ],
        email: [
          { required: true, message: 'Email is required' },
          { type: 'email', message: 'Email is not a valid email' },
          { validator: validateEmail, trigger: 'blur' },
        ],
        status: [{ required: true, message: 'Status is required' }],
        password: [
          { required: true, message: 'Password is required' },
          {
            min: 6,
            message: 'Length should be greater than 6',
            trigger: 'blur',
          },
          { validator: validatePassword, trigger: 'blur' },
        ],
        confirmPassword: [
          { required: true, message: 'Confirm password is required' },
          {
            min: 6,
            message: 'Length should be greater than 6',
            trigger: 'blur',
          },
          { validator: validateConfirmPassword, trigger: 'blur' },
        ],
        allowedBranches: [{ required: true, message: 'Branches is required' }],
        roles: [{ required: true, message: 'Branches is required' }],
      },
    }
  },
  mounted() {
    this.getRoles()
    this.getBranches()
    this.getDataUpdate()
  },
  methods: {
    getRoles() {
      lookupRole
        .callPromise()
        .then(result => {
          this.roleOpts = result
        })
        .catch(error => {
          Notify.error({ message: error.reason })
        })
    },
    getBranches() {
      lookupBranch
        .callPromise({ selector: {} })
        .then(result => {
          this.branchPermissionOpts = result
        })
        .catch(error => {
          Notify.error({ message: error.reason })
        })
    },
    getDataUpdate() {
      this.loading = true
      const _id = this.updateId
      findOneUser
        .callPromise({ _id })
        .then(result => {
          // Orange result
          this.form = {
            _id: result._id,
            fullName: result.profile.fullName,
            username: result.username,
            email: result.emails[0].address,
            status: result.profile.status,
            password: '',
            confirmPassword: '',
            allowedBranches: result.profile.allowedBranches,
            roles: result.roles,
          }
          this.loading = false
        })
        .catch(error => {
          this.loading = false
          Notify.error({ message: error })
        })
    },
    submitForm() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          this.loading = true

          // Make data
          const data = _.clone(this.form)
          delete data.confirmPassword
          updateUser
            .callPromise({ user: data })
            .then(result => {
              if (result) {
                this.loading = false
                if (result === 'logout') {
                  this.$store.commit('app/logout', this)
                } else {
                  Msg.success()
                  this.handleClose()
                }
              }
            })
            .catch(error => {
              this.loading = false
              Notify.error({ message: error.reason })
            })
        } else {
          return false
        }
      })
    },
    resetForm() {
      this.$refs['form'].resetFields()
    },

    handleClose() {
      this.$emit('modal-close')
    },
  },
}
