import _ from 'lodash'
import moment from 'moment'

import Notify from '/imports/client/lib/notify'
import wrapCurrentTime from '/imports/client/lib/wrap-current-time'
import Lookup from '../lib/Lookup-Value'
import { lookupClass } from '/imports/utils/lookup-methods'
import { findStudentDebt } from '/imports/modules/school/api/report/studentDebt'
import { Printd } from 'printd'
import toCss from 'to-css'
import { dateRangePickerOpts } from '/imports/client/lib/date-range-picker-opts'

// const toCss = require('to-css')
import reportCSS from '../styles/reportCss'

export default {
  name: 'StudentDebt',
  data() {
    return {
      totalAll: 0,
      totalFemale: 0,
      typeOpts: Lookup.type,
      reportDate: moment().toDate(),
      statusOpts: [
        { label: 'Not Paid', value: 'Expire' },
        { label: 'Debt', value: 'Debt' },
        // { label: 'Paid', value: 'Paid' },
      ],
      form: {
        status: '',
        type: '',
        dateOpt:[moment().toDate(), moment().toDate()],

      },
      pickerOptions:dateRangePickerOpts,

      loading: false,
      tableData: [],
      titles: [
        { label: 'Student', prop: 'studentName' },
        { label: 'Gender', prop: 'gender', width: '100px' },
        { label: 'Subject', prop: 'subject' },
        { label: 'Room', prop: 'roomName' },
        { label: 'Time', prop: 'timeStudy' },
        { label: 'End Date', prop: 'endPayDate' },
        { label: 'Type', prop: 'type' },
      ],
      itemProp: false,
      infoShow: false,
      teacherName: '',
      roomName: '',
      subjectName: '',
      timeStudy: [],

      // classIdOpts: [],
      // form: {
      //   start: '',
      //   end: '',
      // },
      rules: {
        status: [{ required: true }],
        type: [{ required: true }],
        dateOpt:[{required:true}]
      },
    }
  },
  mounted() {
    this.d = new Printd()
    // this.getClassData()
  },
  methods: {
    getData() {
      this.tableData = []
      for (let i = 1; i <= 100; i++) {
        this.tableData.push({
          student: 'Logn Dara' + i,
          gender: 'Male',
          payDate: '12/03/2018',
          endPayDate: '2018/2/' + i,
        })
      }
    },
    handleOptChange(val) {
      if (val === '2') {
        this.itemProp = true
      } else {
        this.itemProp = false
      }
    },
    getClassData() {
      lookupClass
        .callPromise({})
        .then(result => {
          this.classIdOpts = result
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },
    handleSubmit() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          this.loading = true
          let dateF = moment(this.form.dateOpt[0]).startOf('day').toDate()
          let dateT = moment(this.form.dateOpt[1]).endOf('day').toDate() 

          let selector = {
            type: { $in: this.form.type },
            status: this.form.status,
            tranDate:{
              $gte:dateF,
              $lte:dateT
            }
          }
          this.totalAll = 0
          this.totalFemale = 0
          findStudentDebt
            .callPromise({ selector })
            .then(result => {
              if (result.length > 0) {
                this.tableData = result
                _.forEach(result, o => {
                  this.totalAll += 1
                  if (o.gender == 'Female') {
                    this.totalFemale += 1
                  }
                })
              } else {
                this.tableData = []
                this.totalAll = 0
                this.totalFemale = 0
              }
              this.loading = false
            })
            .catch(error => {
              Notify.error({ message: error })
            })
        } else {
          return false
        }
      })
    },
    handlePrint() {
      // font-family: Times New Roman, Times, serif;
      // font-family: Moul, Helvetica Neue, Helvetica, PingFang SC, Hiragino Sans GB, Microsoft YaHei, SimSun, sans-serif !important;

      const reportCSS = `
      .logo>img {
          float:left;
          // position: absolute;
        }
      img{
         width: 140px;
          height: 100px;
      }
        .report-header>header{
          content: "";
          clear: both;
          // display: table;
        }
      .header {

          text-align: center;
          padding-bottom: 10px;
          width: 80%;
          margin-left:20px;
        }
        .header >.headerKhmer {

            font-size: 30px;
             color: darkgray;
            font-weight: 500;
            padding-top: 10px;
            font-family: 'Moul', PingFang SC, Hiragino Sans GB, Microsoft YaHei, SimSun, sans-serif !important;
        }
        .header >.headerEn {
          font-size: 25px;
          font-family: Times New Roman;
          color: darkgray;

        }
        .table-content {
          border-collapse: collapse;
          width: 100%;
        }
        .info-class {
          position: relative;
        margin-top: 4vh;
      }

      .info-class>.clLeft {
          font-size: 14px;
          position: absolute;
          top: 35%;
          width: 20%;
          left: 10%;
      }

      .info-class>.clCenter {
          font-size: 14px;
          position: absolute;
          margin-left: 10vh;
          top: 35%;
          left: 35%;
      }

      .info-class>.clRight {
          font-size: 14px;
          position: absolute;
          left: 80%;
          top: 35%;
      }
      .tableShow {
          font-family: sans-serif;
          font-size: 12px;
          // margin-top: 10vh;
      }
       .tableShow .table-content,
      th,
      td {
          border: 1px solid #ddd;
          border-bottom: 1px solid #ddd;
          padding: 5px;
      }

       .tableShow th {
          background-color: #ddd;
        }

        .title {
            text-align: center;
            font-weight: 700;
            color: black;
        }
        .info-header {
            margin-top: 2vh;
            height: 4vh;
            text-align: center;
            font-size: 14px;
            font-weight: 500;
            font-style: initial oblique;
        }
        // hide repeat header
        thead
        {
            display: table-row-group;
        }
      `
      this.d.print(document.getElementById('tableStudent'), reportCSS)
    },
    formatDate(val) {
      return moment(val).format('DD/MM/YYYY')
    },
    formatTime(val) {
      let data = val
        .map(o => {
          return moment(o).format('LT')
        })
        .join('-')
      return data
    },
  },
}
