import Msg from '/imports/client/lib/message'
import Notify from '/imports/client/lib/notify'
import _ from 'lodash'
import moment from 'moment'
import LookUp from '../lib/Lookup-Value'
import { lookupCategories } from '/imports/utils/lookup-methods'
import { insertItems, updateItems } from '/imports/modules/school/api/items/methods'
import wrapCurrentTime from '/imports/client/lib/wrap-current-time'
export default {
  name: 'ItemForm',
  props: {
    formType: {
      type: String,
      default: 'Add New',
    },
    updateDoc: {
      type: Object,
      default: null,
    },
  },
  data() {
    return {
      loading: false,
      statusOpts: LookUp.status,
      categoryIdOpts: [],
      form: {
        name: '',
        categoryId: '',
        status: '',
      },
      rules: {
        name: [
          {
            required: true,
            message: 'Please Input Name',
            trigger: 'chnage',
          },
        ],
        categoryId: [
          {
            required: true,
            message: 'Please select category',
            trigger: 'blur',
          },
        ],
        status: [
          {
            required: true,
            message: 'Please select status',
            trigger: 'blur',
          },
        ],
      },
    }
  },
  watch: {
    updateDoc(val) {
      if (val) {
        this.form = val
      }
    },
  },
  mounted() {
    this.getCategory()
  },
  methods: {
    getCategory() {
      lookupCategories
        .callPromise({})
        .then(result => {
          if (result) {
            this.categoryIdOpts = result
          }
        })
        .catch(err => {
          Notify.error({ message: err })
        })
    },
    handleSave() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.formType === 'Add New') {
            this.loading = true
            insertItems
              .callPromise(this.form)
              .then(result => {
                if (result) {
                  this.loading = false
                  Msg.success()
                  this.handleCancel()
                }
              })
              .catch(err => {
                Notify.error({ message: err })
              })
          } else if (this.formType === 'Update') {
            this.loading = true
            updateItems
              .callPromise(this.form)
              .then(result => {
                if (result) {
                  this.loading = false
                  Msg.success()
                  this.handleCancel()
                }
              })
              .catch(err => {
                Notify.error({ message: err })
              })
          }
        } else {
          return false
        }
      })
    },
    resetForm() {
      this.$refs['form'].resetFields()
    },
    handleCancel() {
      this.resetForm()
      this.form._id = null
      this.$emit('data-change')
    },
    formatTime(val) {
      return moment(val).format('hh:mm a')
    },
  },
}
