import _ from 'lodash'
import moment from 'moment'
import Msg from '/imports/client/lib/message'
import Notify from '/imports/client/lib/notify'
import wrapCurrentTime from '/imports/client/lib/wrap-current-time'
import SubAcc from '../components/SubAccounting.vue'
const numeral = require('numeral')
//=============================================================
import { findOneIncome, updateIncome } from '/imports/modules/school/api/Income/methods'
export default {
  name: 'Acc',
  components: { SubAcc },
  props: {
    visible: {
      type: Boolean,
      default: false,
    },
    updateId: {
      type: String,
      default: null,
    },
  },
  data() {
    return {
      saveEvent: 0,
      referenceTypeOpts: [
        { label: 'Income', value: 'Income' },
        { label: 'Expend', value: 'Expend' },
      ],
      totalUsd: 0,
      totalKhr: 0,
      itemsProp: this.initItems(),
      form: {
        tranDate: moment().toDate(),
        referenceType: '',
        totalUsd: 0,
        totalKhr: 0,
      },
      rules: {
        tranDate: { required: true },
        referenceType: { required: true },
      },
    }
  },
  mounted() {
    this.getIncomeUpdate()
  },
  methods: {
    getIncomeUpdate() {
      findOneIncome
        .callPromise({ id: this.updateId })
        .then(result => {
          this.form = result.data
          this.itemsProp = result.details

          _.forEach(this.itemsProp, o => {
            if (result.data.referenceType === 'Expend') {
              o.usd = -o.usd
              o.khr = -o.khr
            }
            delete o._id
            delete o.tranDate
            delete o.referenceType
            delete o.referenceId
          })
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },
    initItems() {
      return [
        {
          name: '',
          usd: 0,
          khr: 0,
        },
      ]
    },
    handleSubFormChange(item) {
      let totalKHR = 0,
        totalUSD = 0
      _.forEach(item, o => {
        totalKHR += o.khr
        totalUSD += o.usd
      })
      this.totalUsd = totalUSD
      this.form.totalUsd = totalUSD
      this.totalKhr = totalKHR
      this.form.totalKhr = totalKHR

      this.itemsProp = item
    },
    saveForm(childValid) {
      this.$refs['form'].validate(valid => {
        if (valid && childValid) {
          this.form.tranDate = wrapCurrentTime(this.form.tranDate)
          updateIncome
            .callPromise({ doc: this.form, details: this.itemsProp })
            .then(result => {
              Msg.success()
              this.handleClose()
            })
            .catch(error => {
              Notify.error({ message: error })
            })
        } else {
          return false
        }
      })
    },
    cancelForm() {
      this.$refs['form'].resetFields()
      this.itemsProp = this.initItems()
    },
    handleClose() {
      this.$emit('modal-close')
    },
    handleresetForm() {
      this.$refs['form'].resetFields()
    },
    formatNum(val) {
      return numeral(val).format('0,0.00')
    },
  },
}
