import Notify from '/imports/client/lib/notify'
import MsgBox from '/imports/client/lib/message'
import moment from 'moment'
//
import ExchangeClassInsert from './ExchangeClassInsert.vue'
import ExchangeClassUpdate from './ExchangeClassUpdate.vue'

// Table Action
import TableToolbar from '/imports/client/components/TableToolbar.vue'
import TableAction from '/imports/client/components/TableAction.vue'
import removeMixin from '/imports/client/mixins/remove'

import {
  findExchangeClass,
  removeExchangeClass,
} from '/imports/modules/school/api/exchange-class/methods.js'
export default {
  components: {
    ExchangeClassInsert,
    ExchangeClassUpdate,
    TableToolbar,
    TableAction,
  },
  mixins: [removeMixin],
  data() {
    return {
      loading: false,
      currentModal: null,
      modalVisible: false,
      updateId: null,
      tableSize: 'mini',
      tableData: [],
      titles: [
        // { label: 'ID', prop: '_id' },
        { label: 'Date', prop: 'tranDate' },
        { label: 'From', prop: 'fromRoom' },
        { label: 'To', prop: 'toRoom' },
        { label: 'Student', prop: 'name' },
        // { label: 'Type', prop: 'type' },
        // { label: 'Status', prop: 'status' },
      ],
      tableProps: {
        size: 'mini',
        border: false,
        stripe: false,
        defaultSort: {
          prop: '_id',
          order: 'ascending',
        },
      },
      tableFilters: [
        {
          prop: ['name', 'tranDate'],
          value: '',
        },
      ],
    }
  },
  mounted() {
    this.getData()
  },
  methods: {
    renderTableMoreHeader(h, { column, $index }) {
      return h('h2', { class: 'el-icon-menu popover-icon' })
    },
    getData() {
      this.loading = true
      findExchangeClass
        .callPromise({})
        .then(result => {
          this.tableData = result
          this.loading = false
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },
    // Add new
    addNew() {
      this.modalVisible = true
      this.currentModal = ExchangeClassInsert
    },
    // Table Action
    actionsList() {
      return ['edit', 'remove']
    },
    tableActionClick(command) {
      this[command.action](command.row)
    },
    // Edit Data
    edit(row) {
      this.updateId = row._id
      this.modalVisible = true
      this.currentModal = ExchangeClassUpdate
    },
    remove(row) {
      this.$_removeMixin({
        meteorMethod: removeExchangeClass,
        selector: { _id: row._id },
        successMethod: 'getData',
        loading: 'loading',
        title: row.title,
      })
    },

    handleModalClose() {
      this.getData()
      this.modalVisible = false
      this.$nextTick(() => {
        this.currentModal = null
      })
    },
    formatDate(val) {
      return moment(val).format('DD/MM/YYYY')
    },
  },
}
