import _ from 'lodash'
import moment from 'moment'

import Notify from '/imports/client/lib/notify'
import Lookup from '../../client/lib/Lookup-Value'
import { lookupClass } from '/imports/utils/lookup-methods'
import { findClassForStudenDetails } from '/imports/modules/school/api/payment/methods'
import { Printd } from 'printd'
import toCss from 'to-css'
// const toCss = require('to-css')
import reportCSS from '../styles/reportCss'
export default {
  data() {
    return {
      loading: false,
      tableData: [],
      typeOpts: Lookup.type,
      titles: [
        { label: 'Student', prop: 'student' },
        { label: 'Gender', prop: 'gender', width: '100px' },
        { label: 'Pay Date', prop: 'payDate' },
        { label: 'End Date', prop: 'endPayDate' },
      ],
      currentDate: moment().toDate(),
      itemProp: false,
      infoShow: false,
      teacherName: '',
      roomName: '',
      subjectName: '',
      timeStudy: [],
      classIdOpts: [],
      totalAll: 0,
      totalFemale: 0,

      form: {
        opt: '',
        classId: '',
      },
      rules: {
        type: [{ required: true }],
        classId: [{ required: true }],
      },
    }
  },
  mounted() {
    this.d = new Printd()
    // this.getClassData()
  },
  methods: {
    // getData() {
    //   this.tableData = []
    //   for (let i = 1; i <= 100; i++) {
    //     this.tableData.push({
    //       student: 'Logn Dara' + i,
    //       gender: 'Male',
    //       payDate: '12/03/2018',
    //       endPayDate: '2018/2/' + i,
    //     })
    //   }
    // },
    handleTypeChange(val) {
      let selector = {
        type: val,
      }
      this.classIdOpts = []
      this.form.classId = ''
      lookupClass
        .callPromise({ selector })
        .then(result => {
          if (result) {
            this.classIdOpts = result
          }
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },
    getClassData() {
      let selector = {}
      lookupClass
        .callPromise({})
        .then(result => {
          this.classIdOpts = result
        })
        .catch(error => {
          Notify.error({ message: error })
        })
    },
    handleSubmit() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          this.totalAll = 0
          this.totalFemale = 0
          this.loading = true
          let selector = {
            classId: this.form.classId,
            status: { $ne: 'Closed' },
          }
          findClassForStudenDetails
            .callPromise({ selector })
            .then(result => {
              if (result.length > 0) {
                _.forEach(result, o => {
                  this.teacherName = o.teacher
                  this.roomName = o.room
                  this.subjectName = o.subject
                  this.timeStudy = o.time

                  this.tableData = o.classDetail
                  _.forEach(o.classDetail, o => {
                    this.totalAll += 1
                    if (o.gender == 'Female') {
                      this.totalFemale += 1
                    }
                  })
                })
              } else {
                this.teacherName = ''
                this.roomName = ''
                this.subjectName = ''
                this.timeStudy = []

                this.tableData = []
              }
              this.loading = false
            })
            .catch(error => {
              Notify.error({ message: error })
            })
        } else {
          return false
        }
      })
    },
    handlePrint() {
      // font-family: Times New Roman, Times, serif;
      // font-family: Moul, Helvetica Neue, Helvetica, PingFang SC, Hiragino Sans GB, Microsoft YaHei, SimSun, sans-serif !important;

      const reportCSS = `
      .logo>img {
          float:left;
          // position: absolute;
        }
      img{
         width: 140px;
          height: 100px;
      }
        .report-header>header{
          content: "";
          clear: both;
          // display: table;
        }
      .header {

          text-align: center;
          padding-bottom: 10px;
          width: 80%;
          margin-left:20px;
        }
        .header >.headerKhmer {

            font-size: 30px;
             color: darkgray;
            font-weight: 500;
            padding-top: 10px;
            font-family: 'Moul', PingFang SC, Hiragino Sans GB, Microsoft YaHei, SimSun, sans-serif !important;
        }
        .header >.headerEn {
          font-size: 25px;
          font-family: Times New Roman;
          color: darkgray;

        }
        .table-content {
          border-collapse: collapse;
          width: 100%;
        }
        .info-class {
          position: relative;
          height:10vh;
      }

      .info-class>.clLeft {
          font-size: 14px;
          position: absolute;
          top: 35%;
          max-width: 20%;
          left: 5%;
      }

      .info-class>.clCenter {
          font-size: 14px;
          position: absolute;
          margin-left: 10vh;
          top: 35%;
          left: 25%;
      }

      .info-class>.clRight {
          font-size: 14px;
          position: absolute;
          left: 70%;
          top: 35%;
      }
      .tableShow {
          font-family: sans-serif;
          font-size: 12px;

      }
       .tableShow .table-content,
      th,
      td {
          // border: 1px solid #ddd;
           border: 0.1px solid #606266;
          // border-bottom: 1px solid #ddd;
          border-bottom: 0.1px solid #606266;
          padding: 5px;
      }

       .tableShow th {
          background-color: #ddd;
        }

        .title {
            font-size:14px;
            text-align: center;
            font-weight: 700;
            color: black;
        }

        .info-header {
            margin-top: 2vh;
            height: 4vh;
            text-align: center;
            font-size: 14px;
            font-weight: 500;
            font-style: initial oblique;
        }
        // hide repeat header
        thead
        {
            display: table-row-group;
        }
      `
      this.d.print(document.getElementById('tableStudent'), reportCSS)
    },
    formatDate(val) {
      return moment(val).format('DD/MM/YYYY')
    },
    formatTime(val) {
      let data = val
        .map(o => {
          return moment(o).format('LT')
        })
        .join('-')
      return data
    },
  },
}
