import Notify from '/imports/client/lib/notify'
import MsgBox from '/imports/client/lib/message'

import { levelOpts } from '/imports/modules/school/api/level/methods'

import {
  insertSubject,
  updateSubject,
  findOneSubject,
} from '/imports/modules/school/api/subject/methods.js'

import Lookup from '../lib/Lookup-Value.js'

export default {
  name: 'SubjectForm',
  props: {
    updateId: {
      type: String,
      default: null,
    },
  },
  data() {
    return {
      // Loading
      loading: {
        form: false,
        level: 'Select',
        type: 'Select',
        status: 'Select',
      },
      // Opts
      typeIdOpts: Lookup.type,
      levelOpts: [],
      statusOpts: Lookup.status,
      // Form
      form: {
        title: '',
        type: 'Part_Time',
        levelId: '',
        status: 'Active',
      },
      // Rule
      rules: {
        title: [{ required: true }],
        type: [{ required: true }],
        levelId: [{ required: true }],
        status: [{ required: true }],
      },
    }
  },

  created() {
    this._getLevelOpts()
  },
  mounted() {
    console.log(this.updateId);
    if (this.updateId) {
      this._getDataUpdate()
    }
  },
  methods: {
    _getDataUpdate() {
      this.loading.form = true
      findOneSubject
        .callPromise({ selector: { _id: this.updateId } })
        .then(result => {
          if (result) {
            this.form = result
            this.loading.form = false
          }
        })
        .catch(error => {
          this.loading.form = false
          console.log(error);
          Notify.error({ message: error })
        })
    },
    _getLevelOpts() {
      this.loading.level = 'Loading.....'
      levelOpts
        .callPromise({})
        .then(result => {
          this.levelOpts = result
          this.loading.level = 'Select'
        })
        .catch(error => {
          this.loading.level = 'Select'
          Notify.error({ message: error })
        })
    },
    _submitForm() {
      this.loading.form = true
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.updateId) {
            updateSubject
              .callPromise(this.form)
              .then(result => {
                MsgBox.success('Update Success')
                this.handleClose()
              })
              .catch(error => {
                Notify.error({ message: error })
              })
          } else {
            insertSubject
              .callPromise(this.form)
              .then(result => {
                if (result) {
                  MsgBox.success()
                  this.resetform()
                  this.loading.form = false
                }
              })
              .catch(error => {
                this.loading.form = false
                Notify.error({ message: error })
              })
          }
        } else {
          this.loading.form = false
          return false
        }
      })
    },
    handleClose() {
      this.$emit('modal-close')
    },
    resetform() {
      this.$refs['form'].resetFields()
    },
  },
}
